function (doc, meta) {

  if(doc.Source_User && doc.Destination_User)
    emit([doc.Source_User, doc.Destination_User, "SentTo"]);

  if(doc.Source_IP && doc.Destination_IP)
    emit([doc.Source_IP, doc.Destination_IP, "Transmitted"]);

  if(doc.Application && doc.Source_IP)
    emit([doc.Application, doc.Source_IP, "ConnectedFrom"]);

  if(doc.Application && doc.Destination_IP)
    emit([doc.Application, doc.Destination_IP, "ConnectedTo"]);

  if(doc.Source_User && doc.Application)
    emit([doc.Source_User, doc.Application, "Used"]);

  if(doc.Source_User && doc.Source_IP)
    emit([doc.Source_User, doc.Source_IP, "LoggedOn"]);
}
